/*
1. Створіть реєстраційну форму. Користувач повинен вводити: нік, електронна адреса, стать, вік.
Використовуйте можливості React для роботи з формами. Використовуйте React для валідації, введених
значень.
*/
import './App.css';
import React, { useState, useEffect } from 'react';

function FormRegistration() {
    return (
      <div className="App">
        <SetNickname />
        <SetEmail/>
        <SetAge/>
        <SetSex/>
      </div>
    );
  }

  function SetNickname(props){
    const [nickName, setNickName] = React.useState('');


    const hendlerNickChange = (event) => {
        setNickName(event.target.value);
    }

    const handleSubmit = (event) => {
      event.preventDefault();
      setNickName("");
      alert(`Ваш нікнейм: ${nickName}`);

    }
    return(
      <div>
        <h1>nickname</h1>
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="nick" id="NickName" value={nickName} onChange={hendlerNickChange} required/>
          <input type="submit" value="Submit"></input>
        </form>
      </div>
    )
  }
  function SetEmail(props){
    const [email, setEmail] = React.useState('');
    const hendlerEmailChange = (event) => {
        setEmail(event.target.value);
    }

    const handleSubmit = (event) => {
      event.preventDefault();
      setEmail("");
      alert(`Ваш mail: ${email}`);

    }
    return(
      <div>
        <h1>mail</h1>
        <form onSubmit={handleSubmit}>
          <input type="email" placeholder="email" id="Email" value={email} onChange={hendlerEmailChange} required/>
          <input type="submit" value="Submit"></input>
        </form>
      </div>
    )
  }
  function SetAge(props){
      const [age, setAge] = React.useState('');
        const hendlerAgeChange = (event) => {
            setAge(event.target.value);
        }
        const handleSubmit = (event) => {
            event.preventDefault();
            setAge("");
            alert(`Ваш вік: ${age}`);

        }
        return(
            <div>
                <h1>age</h1>
                <form onSubmit={handleSubmit}>
                    <input type="number" placeholder="age" id="Age" value={age} onChange={hendlerAgeChange} required/>
                    <input type="submit" value="Submit"></input>
                </form>
            </div>
        )
  }
  function SetSex(props){
    const [sex, setSex] = React.useState('');
    const handlerSexChange = (event) => {
        setSex(event.target.value);
    }
    const handleSubmit = (event) => {
        event.preventDefault();
        setSex('');
        alert(`Ваша стать: ${sex}`)
    }
    return(
        <div>
            <form onSubmit={handleSubmit}>
                <p>Please select your sex</p>
                <div>
                    <input type="radio" id="Sex"
                    name="contact" value={sex}/>
                    <label for="Sex">Mail</label>

                    <input type="radio" id="Sex"
                    name="contact" value={sex}/>
                    <label for="contactChoice2">Female</label>
                </div>
                <div>
                    <button type="submit">Submit</button>
                </div>
            </form>
        </div>
    )
  }
  export default FormRegistration;
