import './App.css';
import React, { useState, useEffect } from 'react';

function Test() {
    return (
      <div className="App">
        <Info/>
      </div>
    );
  }
function Info(){
    const [name, setName] = useState('');

    const hendlerNickChange = (event) => {
        setName(event.target.value);
    }
    const handleSubmit = (event) => {
        event.preventDefault();
        alert(name);
    }

    return(
        <div>
            <form onSubmit={handleSubmit}>
            <SetName onAction = {hendlerNickChange} name = {name}/>

            <input type="submit" value="Submit"></input>
            </form>
        </div>
    );

}

function SetName(props ){

    const hendlerNickChange = (event) => {
        props.onAction(props.name);
    }

    return(
        <form>
            <h1>My information</h1>
            <input type="text" placeholder="nick" id="NickName" value={props.name} onChange={hendlerNickChange}/>
            <p>nickname: {props.name}</p>
        </form>
    );
}
  export default Test;
